function toolsCookieCreateCookie(name,value,seconds,baseCount){baseCount=baseCount||1000;var expires;if(seconds){var date=new Date();date.setTime(date.getTime()+(seconds*baseCount));expires="; expires="+date.toGMTString();}else{expires="";}
document.cookie=name+"="+value+expires+"; path=/";}
function toolsCookieCreateCookieMinute(name,value,seconds){toolsCookieCreateCookie(name,value,seconds,60000);}
function toolsCookieReadCookie(name){var nameEQ=name+"=";var ca=document.cookie.split(';');for(var i=0;i<ca.length;i++){var c=ca[i];while(c.charAt(0)==' ')c=c.substring(1,c.length);if(c.indexOf(nameEQ)==0)return c.substring(nameEQ.length,c.length);}
return false;}
function toolsCookieEraseCookie(name){document.cookie=name+"="+"; path=/; expires=Thu, 01-Jan-1970 00:00:01 GMT";}
function toolsStringUcfirst(str){str+='';var tmp=str.charAt(0).toUpperCase();return tmp+str.substr(1);}
function toolsGetSlugFromHref(href){if(typeof href!=='string'){return;}
return href.match(/([^_|/]+_\d+)\.([^/]+)?/)[1];}
function toolsStringMakeSlug(str){var rExps=[{re:/[\xC0-\xC6]/g,ch:'A'},{re:/[\xE0-\xE6]/g,ch:'a'},{re:/[\xC8-\xCB]/g,ch:'E'},{re:/[\xE8-\xEB]/g,ch:'e'},{re:/[\xCC-\xCF]/g,ch:'I'},{re:/[\xEC-\xEF]/g,ch:'i'},{re:/[\xD2-\xD6]/g,ch:'O'},{re:/[\xF2-\xF6]/g,ch:'o'},{re:/[\xD9-\xDC]/g,ch:'U'},{re:/[\xF9-\xFC]/g,ch:'u'},{re:/[\xC7-\xE7]/g,ch:'c'},{re:/[\xD1]/g,ch:'N'},{re:/[\xF1]/g,ch:'n'}];for(var i=0,len=rExps.length;i<len;i++){str=str.replace(rExps[i].re,rExps[i].ch);}
return str.toLowerCase().replace(/\s+/g,'-').replace(/[^a-z0-9-]/g,'').replace(/\-{2,}/g,'-');}
